import React from 'react';

// Import component
import MyBtn from './MyBtn';

// Dummy component
const MyForm = props => {
	return (
		<form style={styles.myForm}>
			<div style={styles.inputContainer}>
				<input placeholder="Post Title" style={styles.input} />
			</div>
			<div style={styles.inputContainer}>
				<input placeholder="Post Description" style={styles.input} />
			</div>
			<div style={styles.buttonContainer}>
				<MyBtn btnText="Create Post" />
			</div>
		</form>
	);
};

// Export
export default MyForm;

// Styling with CSS-in-JS
const styles = {
	myForm: {
		display: 'flex',
		flexDirection: 'column',
		alignItems: 'center',
		backgroundColor: 'grey',
		color: 'black',
		borderRadius: '10px',
	},
	inputContainer: {
		padding: '10px',
		display: 'flex',
	},
	input: {
		padding: '10px',
		margin: '5px',
		flex: '1',
	},
	buttonContainer: {
		textAlign: 'right',
		padding: '10px',
	},
	button: {
		backgroundColor: 'blue',
		color: 'white',
		padding: '10px',
		cursor: 'pointer',
	},
};
