// Import useState hook
import React, { useState } from 'react';

// Import chartCard components
import CardBarCharts from '../components/chartCards/CardBarCharts';

// Functional component vs. class component
function Dashboard() {
	// Destructuring
	const [cardBarCharts] = useState([
		{ chartHeader: 'Income Overview' },
		{ chartHeader: 'Savings Overview' },
	]);

	console.log(cardBarCharts);

	// map() method returns cardBarCharts array containing CardBarCharts component with new state data
	return (
		<section>
			<h2>Dashboard</h2>
			<section style={styles.section}>
				{cardBarCharts.map((cardBarCharts, id) => (
					<CardBarCharts key={id} id={id} cardBarCharts={cardBarCharts} />
				))}
			</section>
		</section>
	);
}

export default Dashboard;

// Styling with CSS-in-JS
const styles = {
	section: {
		display: 'flex',
		justifyContent: 'space-between',
	},
};
